    	}
    	else
    	{
    		$sHtml = str_pad(str_replace($sPrependedDomainName, "", $sOwner), SPACE_PTR_OWNER_TO_CLASS)." IN PTR ".$this->Get('hostname');
    	}
    	$sComment = $this->Get('comment');
    	if ($sComment == '')
    	{
    		$sHtml .= "\n";
    	}
    	else
    	{
    		$sHtml = str_pad($sHtml, SPACE_TO_COMMENT)." ; ".$this->Get('comment')."\n";
    	}
    	return $sHtml;
   }

