protected function OnInsert()
   {
     parent::OnInsert();

     // Add '"' at the front and the end of txt field if not already set
     $sTxt = $this->Get('txt');
     if (substr($sTxt, - 1) != '"')
     {
        $sTxt = $sTxt.'"';
        if ($sTxt[0] != '"')
        {
           $sTxt = '"'.$sTxt;   
        }        
        $this->Set('txt', $sTxt);        
     }
   }

